// SPDX-License-Identifier: MIT
pragma solidity ^0.8.6;

contract TasksContract {
    constructor() {
        createTask("Test Title", "Test description");
    }

    event TaskCreated(
        uint256 id,
        string title,
        string description,
        bool done,
        uint256 createdAt
    );

    uint256 public taskCounter = 0;

    struct Task {
        uint256 id;
        string title;
        string description;
        bool done;
        uint256 createdAt;
    }

    mapping(uint256 => Task) public Tasks;

    uint256[] public taskList;

    function getTasks() external view returns (Task[] memory) {
        Task[] memory _tasks = new Task[](taskList.length);
        for (uint256 i = 0; i < taskList.length; i++) {
            _tasks[i] = Task(
                _tasks[taskList[i]].id,
                _tasks[taskList[i]].title,
                _tasks[taskList[i]].description,
                _tasks[taskList[i]].done,
                _tasks[taskList[i]].createdAt
            );
        }
        return _tasks;
    }

    function createTask(string memory _title, string memory _description)
        public
    {
        taskCounter++;
        Tasks[taskCounter] = Task(
            taskCounter,
            _title,
            _description,
            false,
            block.timestamp
        );
        emit TaskCreated(
            taskCounter,
            _title,
            _description,
            false,
            block.timestamp
        );
    }

    function deleteTask(uint256 _id) public {
        delete Tasks[_id];
    }

    function modifyTask(uint256 _id) public {
        Task memory _task = Tasks[_id];
        _task.done = !_task.done;
        Tasks[_id] = _task;
        emit TaskCreated(
            _task.id,
            _task.title,
            _task.description,
            _task.done,
            _task.createdAt
        );
    }
}
